$(".form").find("input, textarea").on("keyup blur focus",function(c){var b=$(this),a=b.prev("label");if(c.type==="keyup"){if(b.val()===""){a.removeClass("active highlight")}else{a.addClass("active highlight")}}else{if(c.type==="blur"){if(b.val()===""){a.removeClass("active highlight")}else{a.removeClass("highlight")}}else{if(c.type==="focus"){if(b.val()===""){a.removeClass("highlight")}else{if(b.val()!==""){a.addClass("highlight")}}}}}});$(".tab a").on("click",function(a){a.preventDefault();$(this).parent().addClass("active");$(this).parent().siblings().removeClass("active");target=$(this).attr("href");$(".tab-content > div").not(target).hide();$(target).fadeIn(600)});